import axios from 'axios';
import { showAlert } from './showAlert';

function addTodoUI(n, d, t) {
    const tlContainer = document.createElement('div');
    tlContainer.id = n;
    tlContainer.className = 'tl__container';

    const ul = document.createElement('ul');
    ul.className = 'tl__list';

    const item = document.createElement('li');
    item.className = 'tl__item';

    const spanItem = document.createElement('span');
    spanItem.className = 'tl__item--item';
    spanItem.textContent = n;

    const spanDes = document.createElement('span');
    spanDes.className = 'tl__item--description';
    spanDes.textContent = d;
    const time = document.createElement('li');
    time.textContent = t
    time.className = 'tl__item tl__item--time';

    item.appendChild(spanItem);
    item.appendChild(spanDes);

    ul.appendChild(item);
    ul.appendChild(time);

    const tlActions = document.createElement('div');
    tlActions.className = 'tl__actions';

    const tlEdit = document.createElement('button');
    tlEdit.textContent = 'edit';
    tlEdit.className = 'tl__btn--edit tl__btn';

    const tlDelete = document.createElement('button');
    tlDelete.textContent = 'delete';
    tlDelete.className = 'tl__btn--delete tl__btn';
    tlDelete.addEventListener('click', e => {
        e.preventDefault();
        deleteTodo(tlContainer.id);
    });

    tlActions.appendChild(tlEdit);
    tlActions.appendChild(tlDelete);

    tlContainer.appendChild(ul);
    tlContainer.appendChild(tlActions);

    document.querySelector('.tl').appendChild(tlContainer);
};

// function editTodoUI(name) {
//     const tlContainer = document.getElementById(name);
//     console.log(tlContainer.childNodes());
// };

function removeTodo(id) {
    document.getElementById(id).remove();
};
export const addTodo = async(name, description, time) => {
    try {
        const resTodo = await axios({
            method: 'POST',
            url: '/api/v1/todos/createAndAddTodo',
            data: {
                name,
                description,
                time
            }
        });
        if (resTodo.data.status === 'success') {
            showAlert('Successfully added.', 'success');
            addTodoUI(name, description, time);
        };
    } catch (err) {
        showAlert(err.response.data.message, 'fail');
    }
};

export const deleteTodo = async(name) => {
    try {
        const res = await axios({
            method: 'DELETE',
            url: `/api/v1/todos/${name}`,
            data: null
        });
        if (res.status === 204) {
            showAlert('Successfully deleted.', 'success');
            removeTodo(name);
        }
    } catch (err) {
        showAlert(err.response.data.message, 'fail');

    }
};

